sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (newValue) {
        var ga = new GlideAjax('x_393513_needit.whenNeededDate');
        ga.addParam('sysparm_name', 'isDatePast');
        ga.addParam('sysparm_date', g_form.getValue('u_when_needed'));
        ga.getXML(checkDate);
    }

    function checkDate(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
        if (answer == 'false') {
            g_form.setValue('u_when_needed', '');
            g_form.showFieldMsg("u_when_needed", "Business Implementation Date must have a current or future date.", "error");
        } /*else {
				var dateTimeStr = g_form.getValue('u_when_needed');
				var dateArr = dateTimeStr.split(' ');
				var date = dateArr[0];
			g_form.setValue("u_when_needed", date);
        }*/
    }
}
